import React, { useState, useEffect } from 'react'
import { Input, Popover, Radio } from 'antd'
import { SettingOutlined } from '@ant-design/icons'

import tokenList from '../tokenList.json'

function Swap() {

  const [slippage, setSlippage] = useState(2.5)
  const [tokenOneAmount, setTokenOneAmount] = useState(0)
  const [tokenTwoAmount, setTokenTwoAmount] = useState(0)
  const [tokenOne, setTokenOne] = useState(tokenList[0])
  const [tokenTwo, setTokenTwo] = useState(tokenList[1])


  const handleSlippageChange = (e) => {
    setSlippage(e.target.value)
  }

  const changeAmount = e => {
    setTokenOneAmount(e.target.value)
  }

  const settings = (
    <>
      <div>Slippage Tollerane</div>
      <div>
        <Radio.Group value={slippage} onChange={handleSlippageChange}>
          <Radio.Button value={0.5}>0.5%</Radio.Button>
          <Radio.Button value={2.5}>2.5%</Radio.Button>
          <Radio.Button value={5}>5.0%</Radio.Button>
        </Radio.Group>
      </div>
    </>
  )

  return (
    <div className='tradeBox' style={{margin: 'auto'}}>
      <div className='tradeBoxHeader'>
        <h4>Swap</h4>
        <Popover
          title="Settings"
          placement='bottomRight'
          trigger="click"
          content={settings}
        >
          <SettingOutlined className='cog' />
        </Popover>
      </div>

      <div className='inputs'>
        <Input placeholder='0' value={tokenOneAmount} onChange={changeAmount} />
        <Input placeholder='0' value={tokenTwoAmount} disabled />

        <div className='assetOne'>
          <img src={tokenOne.img} alt="Token Logo" className='assetLogo' />
          { tokenOne.ticker }
        </div>

        <div className='assetTwo'>
          <img src={tokenTwo.img} alt="Token Logo" className='assetLogo' />
          { tokenTwo.ticker}
        </div>
      </div>
    </div>
  )
}

export default Swap
